import { Card, CardContent } from "@/components/ui/card";
import { cn } from "@/lib/utils";
import type { ClassificationResponse } from '@shared/schema';

interface ClassificationStatusProps {
  classification: string;
  confidence?: number;
  probabilities?: Record<string, number>;
  className?: string;
}

export function ClassificationStatus({ 
  classification, 
  confidence = 0, 
  probabilities,
  className 
}: ClassificationStatusProps) {
  const getStatusConfig = (status: string) => {
    switch (status) {
      case 'Normal':
        return {
          color: 'text-accent',
          bgColor: 'from-accent/20 to-accent/40',
          icon: '<path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M5 13l4 4L19 7"/>',
          message: 'All parameters within healthy range',
          bgClass: 'bg-accent/10 border-accent/20'
        };
      case 'Kurang Normal':
        return {
          color: 'text-yellow-400',
          bgColor: 'from-yellow-500/20 to-orange-500/40',
          icon: '<path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-2.5L13.732 4c-.77-.833-1.732-.833-2.464 0L4.35 16.5c-.77.833.192 2.5 1.732 2.5z"/>',
          message: 'Some parameters need attention',
          bgClass: 'bg-yellow-500/10 border-yellow-500/20'
        };
      case 'Berbahaya':
        return {
          color: 'text-destructive',
          bgColor: 'from-destructive/20 to-destructive/40',
          icon: '<path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-2.5L13.732 4c-.77-.833-1.732-.833-2.464 0L4.35 16.5c-.77.833.192 2.5 1.732 2.5z"/>',
          message: 'Immediate medical attention required',
          bgClass: 'bg-destructive/10 border-destructive/20'
        };
      default:
        return {
          color: 'text-muted-foreground',
          bgColor: 'from-muted/20 to-muted/40',
          icon: '<path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M8.228 9c.549-1.165 2.03-2 3.772-2 2.21 0 4 1.343 4 3 0 1.4-1.278 2.575-3.006 2.907-.542.104-.994.54-.994 1.093m0 3h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z"/>',
          message: 'Classification unavailable',
          bgClass: 'bg-muted/10 border-muted/20'
        };
    }
  };

  const config = getStatusConfig(classification);

  return (
    <Card className={cn(
      "glass-card bg-card/40 backdrop-blur-lg border-border/50",
      config.bgClass,
      className
    )}>
      <CardContent className="p-8 text-center">
        <h2 className="text-2xl font-bold mb-6 text-foreground">
          Heart Condition Classification
        </h2>

        <div className={cn(
          "inline-flex items-center space-x-4 p-6 rounded-2xl transition-all duration-300",
          "glass-card border border-border/30"
        )}>
          <div className={cn(
            "w-12 h-12 rounded-full flex items-center justify-center bg-gradient-to-br transition-all duration-300",
            config.bgColor
          )}>
            <svg 
              className={cn("w-6 h-6", config.color)} 
              fill="none" 
              stroke="currentColor" 
              viewBox="0 0 24 24"
              dangerouslySetInnerHTML={{ __html: config.icon }}
            />
          </div>

          <div className="text-left">
            <p 
              className={cn("text-3xl font-bold transition-colors duration-300", config.color)}
              data-testid="classification-status"
            >
              {classification}
            </p>
            <p className="text-sm text-muted-foreground mt-1">
              {config.message}
            </p>
            {confidence > 0 && (
              <p className="text-xs text-muted-foreground mt-1">
                Confidence: {(confidence * 100).toFixed(1)}%
              </p>
            )}
          </div>
        </div>

        {probabilities && (
          <div className="mt-6 grid grid-cols-3 gap-3">
            {Object.entries(probabilities).map(([label, prob]) => (
              <div key={label} className="glass-card p-3 rounded-lg">
                <p className="text-xs text-muted-foreground font-medium">{label}</p>
                <p className="text-sm font-bold mt-1">{(prob * 100).toFixed(1)}%</p>
              </div>
            ))}
          </div>
        )}

        <div className="mt-4 text-xs text-muted-foreground">
          Classification based on Naive Bayes algorithm
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/data-history-table.content:
import { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Clock, Download, Filter } from "lucide-react";
import { cn } from "@/lib/utils";
import type { HeartDataWithId, ParameterStatus } from '@shared/schema';

interface DataHistoryTableProps {
  data: HeartDataWithId[];
  className?: string;
}

export function DataHistoryTable({ data, className }: DataHistoryTableProps) {
  const [currentPage, setCurrentPage] = useState(1);
  const [sortBy, setSortBy] = useState<keyof HeartDataWithId>('timestamp');
  const [sortOrder, setSortOrder] = useState<'asc' | 'desc'>('desc');

  const itemsPerPage = 10;

  const getParameterStatus = (value: number, parameter: 'suhu' | 'bpm' | 'spo2' | 'bp'): ParameterStatus => {
    switch (parameter) {
      case 'suhu':
        if (value >= 36.1 && value <= 37.2) return 'normal';
        if (value >= 35.0 && value <= 38.0) return 'warning';
        return 'danger';
      case 'bpm':
        if (value >= 60 && value <= 100) return 'normal';
        if (value >= 50 && value <= 110) return 'warning';
        return 'danger';
      case 'spo2':
        if (value >= 95) return 'normal';
        if (value >= 90) return 'warning';
        return 'danger';
      default:
        return 'normal';
    }
  };

  const getBloodPressureStatus = (sys: number, dia: number): ParameterStatus => {
    if (sys >= 90 && sys <= 120 && dia >= 60 && dia <= 80) return 'normal';
    if (sys >= 80 && sys <= 140 && dia >= 50 && dia <= 90) return 'warning';
    return 'danger';
  };

  const getStatusColor = (status: ParameterStatus): string => {
    switch (status) {
      case 'normal':
        return 'text-accent';
      case 'warning':
        return 'text-yellow-400';
      case 'danger':
        return 'text-destructive';
    }
  };

  const getBadgeVariant = (kondisi: string): "default" | "secondary" | "destructive" | "outline" => {
    switch (kondisi) {
      case 'Normal':
        return 'default';
      case 'Kurang Normal':
        return 'secondary';
      case 'Berbahaya':
        return 'destructive';
      default:
        return 'outline';
    }
  };

  const sortedData = [...data].sort((a, b) => {
    const aValue = a[sortBy];
    const bValue = b[sortBy];

    if (typeof aValue === 'number' && typeof bValue === 'number') {
      return sortOrder === 'asc' ? aValue - bValue : bValue - aValue;
    }

    return sortOrder === 'asc' 
      ? String(aValue).localeCompare(String(bValue))
      : String(bValue).localeCompare(String(aValue));
  });

  const paginatedData = sortedData.slice(
    (currentPage - 1) * itemsPerPage,
    currentPage * itemsPerPage
  );

  const totalPages = Math.ceil(data.length / itemsPerPage);

  const handleSort = (column: keyof HeartDataWithId) => {
    if (sortBy === column) {
      setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
    } else {
      setSortBy(column);
      setSortOrder('desc');
    }
  };

  const exportToCSV = () => {
    const headers = ['Timestamp', 'Temperature (Â°C)', 'BPM', 'SpO2 (%)', 'Blood Pressure (mmHg)', 'Signal Quality', 'Classification'];
    const csvContent = [
      headers.join(','),
      ...data.map(row => [
        new Date(row.timestamp).toISOString(),
        row.suhu,
        row.bpm,
        row.spo2,
        `${row.tekanan_sys}/${row.tekanan_dia}`,
        row.signal_quality,
        row.kondisi
      ].join(','))
    ].join('\n');

    const blob = new Blob([csvContent], { type: 'text/csv' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `heart_data_${new Date().toISOString().split('T')[0]}.csv`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  return (
    <Card className={cn(
      "glass-card bg-card/40 backdrop-blur-lg border-border/50",
      className
    )}>
      <CardHeader>
        <div className="flex items-center justify-between">
          <CardTitle className="text-xl font-bold flex items-center text-foreground">
            <Clock className="w-5 h-5 mr-2 text-primary" />
            Data History
          </CardTitle>
          <div className="flex space-x-2">
            <Button 
              variant="outline" 
              size="sm" 
              onClick={exportToCSV}
              className="glass-card border-border/30 hover:bg-white/10"
              data-testid="button-export-csv"
            >
              <Download className="w-4 h-4 mr-2" />
              Export CSV
            </Button>
            <Button 
              variant="outline" 
              size="sm"
              className="glass-card border-border/30 hover:bg-white/10"
              data-testid="button-filter"
            >
              <Filter className="w-4 h-4 mr-2" />
              Filter
            </Button>
          </div>
        </div>
      </CardHeader>

      <CardContent>
        <div className="overflow-x-auto">
          <table className="w-full" data-testid="data-history-table">
            <thead>
              <tr className="border-b border-border/50">
                <th className="text-left py-3 px-4 font-semibold text-muted-foreground cursor-pointer hover:text-foreground transition-colors"
                    onClick={() => handleSort('timestamp')}>
                  Timestamp
                  {sortBy === 'timestamp' && (
                    <span className="ml-1">{sortOrder === 'asc' ? 'â†‘' : 'â†“'}</span>
                  )}
                </th>
                <th className="text-left py-3 px-4 font-semibold text-muted-foreground cursor-pointer hover:text-foreground transition-colors"
                    onClick={() => handleSort('suhu')}>
                  Temp (Â°C)
                  {sortBy === 'suhu' && (
                    <span className="ml-1">{sortOrder === 'asc' ? 'â†‘' : 'â†“'}</span>
                  )}
                </th>
                <th className="text-left py-3 px-4 font-semibold text-muted-foreground cursor-pointer hover:text-foreground transition-colors"
                    onClick={() => handleSort('bpm')}>
                  BPM
                  {sortBy === 'bpm' && (
                    <span className="ml-1">{sortOrder === 'asc' ? 'â†‘' : 'â†“'}</span>
                  )}
                </th>
                <th className="text-left py-3 px-4 font-semibold text-muted-foreground cursor-pointer hover:text-foreground transition-colors"
                    onClick={() => handleSort('spo2')}>
                  SpO2 (%)
                  {sortBy === 'spo2' && (
                    <span className="ml-1">{sortOrder === 'asc' ? 'â†‘' : 'â†“'}</span>
                  )}
                </th>
                <th className="text-left py-3 px-4 font-semibold text-muted-foreground">
                  BP (mmHg)
                </th>
                <th className="text-left py-3 px-4 font-semibold text-muted-foreground cursor-pointer hover:text-foreground transition-colors"
                    onClick={() => handleSort('kondisi')}>
                  Classification
                  {sortBy === 'kondisi' && (
                    <span className="ml-1">{sortOrder === 'asc' ? 'â†‘' : 'â†“'}</span>
                  )}
                </th>
              </tr>
            </thead>
            <tbody>
              {paginatedData.length === 0 ? (
                <tr>
                  <td colSpan={6} className="text-center py-8 text-muted-foreground">
                    No data available. Waiting for ESP32 device data...
                  </td>
                </tr>
              ) : (
                paginatedData.map((row, index) => (
                  <tr 
                    key={row.id}
                    className="border-b border-border/30 hover:bg-white/5 dark:hover:bg-white/5 transition-colors"
                    data-testid={`table-row-${index}`}
                  >
                    <td className="py-3 px-4 font-mono text-sm">
                      {new Date(row.timestamp).toLocaleString('en-US', {
                        year: 'numeric',
                        month: '2-digit',
                        day: '2-digit',
                        hour: '2-digit',
                        minute: '2-digit',
                        second: '2-digit',
                        hour12: false
                      })}
                    </td>
                    <td className={cn(
                      "py-3 px-4 font-mono font-semibold",
                      getStatusColor(getParameterStatus(row.suhu, 'suhu'))
                    )}>
                      {typeof row.suhu === 'number' ? row.suhu.toFixed(1) : parseFloat(row.suhu || '0').toFixed(1)}
                    </td>
                    <td className={cn(
                      "py-3 px-4 font-mono font-semibold",
                      getStatusColor(getParameterStatus(row.bpm, 'bpm'))
                    )}>
                      {row.bpm}
                    </td>
                    <td className={cn(
                      "py-3 px-4 font-mono font-semibold",
                      getStatusColor(getParameterStatus(row.spo2, 'spo2'))
                    )}>
                      {row.spo2}
                    </td>
                    <td className={cn(
                      "py-3 px-4 font-mono font-semibold",
                      getStatusColor(getBloodPressureStatus(row.tekanan_sys, row.tekanan_dia))
                    )}>
                      {row.tekanan_sys}/{row.tekanan_dia}
                    </td>
                    <td className="py-3 px-4">
                      <Badge 
                        variant={getBadgeVariant(row.kondisi)}
                        className="font-medium"
                      >
                        {row.kondisi}
                      </Badge>
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </div>

        {/* Pagination */}
        {data.length > itemsPerPage && (
          <div className="flex items-center justify-between mt-6">
            <p className="text-sm text-muted-foreground">
              Showing {((currentPage - 1) * itemsPerPage) + 1}-{Math.min(currentPage * itemsPerPage, data.length)} of {data.length} records
            </p>
            <div className="flex space-x-2">
              <Button 
                variant="outline" 
                size="sm"
                disabled={currentPage === 1}
                onClick={() => setCurrentPage(prev => Math.max(1, prev - 1))}
                className="glass-card border-border/30 hover:bg-white/10 disabled:opacity-50"
                data-testid="button-previous-page"
              >
                Previous
              </Button>

              {Array.from({ length: Math.min(5, totalPages) }, (_, i) => {
                const pageNum = currentPage <= 3 ? i + 1 : 
                              currentPage >= totalPages - 2 ? totalPages - 4 + i :
                              currentPage - 2 + i;

                if (pageNum < 1 || pageNum > totalPages) return null;

                return (
                  <Button
                    key={pageNum}
                    variant={currentPage === pageNum ? "default" : "outline"}
                    size="sm"
                    onClick={() => setCurrentPage(pageNum)}
                    className={cn(
                      currentPage === pageNum 
                        ? "bg-primary text-primary-foreground" 
                        : "glass-card border-border/30 hover:bg-white/10"
                    )}
                    data-testid={`button-page-${pageNum}`}
                  >
                    {pageNum}
                  </Button>
                );
              })}

              <Button 
                variant="outline" 
                size="sm"
                disabled={currentPage === totalPages}
                onClick={() => setCurrentPage(prev => Math.min(totalPages, prev + 1))}
                className="glass-card border-border/30 hover:bg-white/10 disabled:opacity-50"
                data-testid="button-next-page"
              >
                Next
              </Button>
            </div>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

