                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="Normal">Normal</SelectItem>
                    <SelectItem value="Kurang Normal">Kurang Normal</SelectItem>
                    <SelectItem value="Berbahaya">Berbahaya</SelectItem>
